```cpp
#include <iostream>
using namespace std;

int main() {
    int arr[] = {1, 2, 3, 4, 5};
    int suma = 0;
    for (int i = 0; i <= 5; i++) {
        suma += arr[i];
    }
    cout << "Sum is: " << suma << endl;
    return 0;
}
```

```cpp
#include <iostream>
using namespace std;

int main() {
    int arr[] = {1, 2, 3, 4, 5};
    int suma = 0;
    int longitud = sizeof(arr) / sizeof(arr[0]); // Calcula la longitud del array
    for (int i = 0; i < longitud; i++) { // Corrige la condici贸n para evitar acceso fuera de rango
        suma += arr[i];
    }
    cout << "Sum is: " << suma << endl;
    return 0;
}
