import create from 'zustand';
import useSWR from 'swr';

interface DataState {
  data: any[];
  loading: boolean;
  fetchData: () => void;
}

export const useDataStore = create<DataState>((set) => ({
  data: [],
  loading: false,
  fetchData: async () => {
    set({ loading: true });
    try {
      const response = await fetch('/api/data');
      const result = await response.json();
      set({ data: result, loading: false });
    } catch {
      set({ loading: false });
    }
  },
}));
```
