/**
 * Operacje ustawie
 */
export interface SettingsAction {
  /**
   * Resetuje ustawienia
   */
  resetSettings: () => void;
  /**
   * Ustawia globalne ustawienia
   * @param settings - globalne ustawienia
   */
  setGlobalSettings: (settings: GlobalSettingsState) => void;
  /**
   * Ustawia czciowe ustawienia konfiguracji
   * @param settings - czciowe ustawienia konfiguracji
   */
  setSettings: (settings: Partial<ConfigSettings>) => void;
  /**
   * Ustawia tryb motywu
   * @param themeMode - tryb motywu
   */
  setThemeMode: (themeMode: ThemeMode) => void;
  /**
   * Przecza opcj paska bocznego
   * @param key - wybrana opcja paska bocznego
   */
  switchSideBar: (key: SidebarTabKey) => void;
}
```
