interface ResetMessages {
  topicId?: string;
  type: "resetMessages";
}

export type MessageDispatch = ResetMessages;

export const messagesReducer = (state: Record<string, any>, action: MessageDispatch) => {
  switch (action.type) {
    case "resetMessages": {
      return produce(state, draft => {
        const topicId = action.topicId;
        for (const key in draft) {
          if (!topicId) {
            if (!draft[key].topicId) {
              delete draft[key];
            }
          } else if (draft[key].topicId === topicId) {
            delete draft[key];
          }
        }
      });
    }
    default:
      throw new Error("–¢–∏–ø –¥–µ–π—Å—Ç–≤–∏—è –Ω–µ —Ä–µ–∞–ª–∏–∑–æ–≤–∞–Ω, –ø—Ä–æ–≤–µ—Ä—å—Ç–µ —Ä–µ–¥—å—é—Å–µ—Ä");
  }
};
```
